mapper.update:
UPDATE tb_userinfo
SET use_yn = "N"
WHERE loginID=#{loginID}

mapper.select:
SELECT lec_id, lec_name, DATE_FORMAT(start_date, '%Y.%m.%d') as start_date, DATE_FORMAT(end_date,'%Y.%m.%d') as end_date FROM tb_lec_info
WHERE lec_name Like CONCAT('%',#{searchWord_lec}, '%')
LIMIT #{pageIndex}, #{pageSize}

UPDATE tb_lec_info
SET pre_pnum = pre_pnum + 1
WHERE lec_id = #{lec_id}

mapper.delete:
DELETE
FROM tb_lecstd_info
WHERE lec_id = #{lec_id}
AND std_id = #{std_id}

mapper.select:
UPDATE tb_lec_info
SET pre_pnum = pre_pnum-1
WHERE lec_id = #{lec_id}

SELECT loginID, name, user_type, mail, tel, DATE_FORMAT(join_date, '%Y.%m.%d') as join_date FROM tb_userinfo u WHERE use_yn = 'Y'
AND user_type='b'
AND user_type=#{user_type}
AND u.join_date between #{from_date} and #{to_date}
AND ( u.name Like CONCAT('%', #{searchWord}, '%') or u.loginID LIKE CONCAT('%', #{searchWord}, '%') or u.tel LIKE CONCAT('%', #{searchWord}, '%') )
AND u.name Like CONCAT('%', #{searchWord}, '%')
AND u.loginID LIKE CONCAT('%', #{searchWord}, '%')
AND u.tel LIKE CONCAT('%', #{searchWord}, '%')
ORDER BY join_date DESC
LIMIT #{pageIndex},#{pageSize}
