mapper.select:
/*kr.happyjob.study.notice.dao.NoticeDao.noticelist*/ select an.notice_id , ui.loginID , ui.name , an.notice_tit , DATE_FORMAT(an.regdate, '%y-%m-%d') as regdate , an.hit from tb_adm_notice an join tb_userinfo ui on ui.loginID = an.loginID
and an.notice_tit Like CONCAT('%', #{searchtitle}, '%')
and an.regdate >= STR_TO_DATE(#{searchstdate}, '%Y-%m-%d')
and an.regdate <= STR_TO_DATE(#{searcheddate}, '%Y-%m-%d')
ORDER BY an.regdate DESC LIMIT #{startpos}, #{pagesize}

mapper.insert:
/*kr.happyjob.study.notice.dao.NoticeDao.noticeSave*/


		insert into tb_adm_notice 
			(	
				 loginID		
				, notice_tit	
				, notice_con	
				, regdate		
				, hit
			) values (
				 #{loginID}
				, #{notice_tit}	
				, #{notice_con}	
				, now()		
				, 0
			)

mapper.update:
UPDATE tb_adm_notice
SET hit = hit+1
WHERE notice_id=#{notice_id};

/*kr.happyjob.study.notice.dao.NoticeDao.noticeModify*/		
	UPDATE tb_adm_notice
	SET
		 notice_tit =#{notice_tit}	
	   , notice_con =#{notice_con}
    WHERE notice_id=#{notice_id};
