mapper.select:
SELECT COUNT(*)
FROM tb_hwk_submit
WHERE std_id=#{loginID}
AND hwk_id=#{hwk_id}

mapper.insert:
/*kr.happyjob.study.notice.dao.NoticeDao.submitSave*/

		insert into tb_hwk_submit 
			(	
				 std_id		
				, hwk_id	
				, submit_con	
				, submit_fname		
				,submit_url
				,submit_fsize
				,submit_date
			) values (
				 #{std_id}
				, #{hwk_id}	
				, #{submit_con}			
				, #{fileinfo.file_nm}	
				, #{fileinfo.file_loc}
				, #{fileinfo.file_size}
				,now()	
			)

mapper.update:
UPDATE tb_hwk_submit
SET
		submit_con =#{modify_con}	
		,submit_fname = #{fileinfo.file_nm}	
		,submit_url = #{fileinfo.file_loc}	
		,submit_fsize = #{fileinfo.file_size}	
		,submit_date = now()
WHERE submit_id=#{submit_id};

mapper.select:
SELECT*
FROM tb_hwk_submit
WHERE std_id=#{std_id}	
AND hwk_id= #{hwk_id}
